package iut.sae.algo;

import org.junit.Test;
import junit.framework.TestCase;


public class AlgoTest extends TestCase{

/*
 * RLE("")=""
 * RLE("abc")="1a1b1c"
 * RLE("abbccc")="1a2b3c"
 * RLE("aaabaa")="3a1b2a"
 * RLE("aAa")="1a1A1a"
 */
   @Test
   public void testRLE(){
      assertEquals("", sobriete.RLE("") );
      
      assertEquals("1a1b1c", sobriete.RLE("abc"));
      assertEquals("1a2b3c", sobriete.RLE("abbccc"));
      assertEquals("3a1b2a", sobriete.RLE("aaabaa"));
      assertEquals("1a1A1a", sobriete.RLE("aAa"));

   }


   /*
   * RLE(str, 1)=RLE(str)
   * RLE(str, 3)=RLE(RLE(RLE(str)))
   */
   @Test
   public void testRLERecursif(){
      try{
         assertEquals("", sobriete.RLE("", 1));
         assertEquals("", sobriete.RLE("", 3));
         
         assertEquals("1a1b1c", sobriete.RLE("abc", 1));
         assertEquals("1a2b3c", sobriete.RLE("abbccc", 1));
         assertEquals("3a1b2a", sobriete.RLE("aaabaa", 1));
         assertEquals("1a1A1a", sobriete.RLE("aAa", 1));

         assertEquals("111a111b111c", (sobriete.RLE("abc", 2)));
         assertEquals("311a311b311c", (sobriete.RLE("abc", 3)));

         String saeIte20=" ";
         assertEquals(saeIte20, sobriete.RLE("SAE Algo", 20));
      }
      catch(Exception e){
         fail("Exception inatendue");
      }
   }

   /*
  * unRLE(RLE(str))=str
  *
  * unRLE("")=""
  * unRLE("1a1b1c")="abc"
  * unRLE("1a2b3c")="abbccc"
  * unRLE("3a1b2a")="aaabaa"
  * unRLE("1a1A1a")="aAa"
  */

   @Test
   public void testUnRLE(){
      try{
         assertEquals("", sobriete.unRLE(""));
         assertEquals("abc", sobriete.unRLE("1a1b1c"));
         assertEquals("abbccc", sobriete.unRLE("1a2b3c"));
         assertEquals("aaabaa", sobriete.unRLE("3a1b2a"));
         assertEquals("aAa", sobriete.unRLE("1a1A1a"));
      }
      catch(Exception e){
         fail("Exception inatendue");
      }
   }
   
   @Test
   public void testUnRLERecursif(){
      try{
         assertEquals("", sobriete.unRLE("", 1));
         assertEquals("", sobriete.unRLE("", 3));
         
         assertEquals("abc", sobriete.unRLE("1a1b1c", 1));
         assertEquals("abbccc", sobriete.unRLE("1a2b3c", 1));
         assertEquals("aaabaa", sobriete.unRLE("3a1b2a", 1));
         assertEquals("aAa", sobriete.unRLE("1a1A1a", 1));

         assertEquals("abc", (sobriete.unRLE("111a111b111c", 2)));
         assertEquals("abc", (sobriete.unRLE("311a311b311c", 3)));
      }
      catch(Exception e){
         fail("Exception inatendue");
      }
   }

   @Test
   public void testSupp(){
      try{
         assertEquals("wwwwwwwwwwww", sobriete.unRLE(sobriete.RLE("wwwwwwwwwwww")));
         assertEquals("aaaaaaaaaaa", sobriete.unRLE(sobriete.RLE("aaaaaaaaaaa")));
         assertEquals("bbbbbbbbbbb", sobriete.unRLE(sobriete.RLE("bbbbbbbbbbb")));
         assertEquals("ccccccccccc", sobriete.unRLE(sobriete.RLE("ccccccccccc")));
         assertEquals("ddddddddddd", sobriete.unRLE(sobriete.RLE("ddddddddddd")));
         assertEquals("eeeeeeeeeee", sobriete.unRLE(sobriete.RLE("eeeeeeeeeee")));
         assertEquals("fffffffffff", sobriete.unRLE(sobriete.RLE("fffffffffff")));
         assertEquals("ggggggggggg", sobriete.unRLE(sobriete.RLE("ggggggggggg")));
         assertEquals("hhhhhhhhhhh", sobriete.unRLE(sobriete.RLE("hhhhhhhhhhh")));
         sobriete.RLE("SAE", 5);
      }
      catch(Exception e){
         fail("Exception inatendue");
      }
   }

}